Drupal.behaviors.contentListing={attach:function(g,u){once("content_card_sort","#paragraph-content-listing-sort #sort-by",g).forEach(n=>{const i=()=>{const t=n.value,s=n.closest(".component--content-listing").querySelector(".nsw-content-listing-items"),o=Array.from(s.children);o.sort((e,r)=>{const a=e.firstElementChild.getAttribute("data-title").toLowerCase(),c=r.firstElementChild.getAttribute("data-title").toLowerCase(),l=new Date(e.firstElementChild.getAttribute("data-date")),d=new Date(r.firstElementChild.getAttribute("data-date"));if(t==="title_asc")return a.localeCompare(c);if(t==="title_desc")return c.localeCompare(a);if(t==="date_asc")return l-d;if(t==="date_desc")return d-l}),s.innerHTML="",o.forEach(e=>s.appendChild(e))};n.addEventListener("change",i),i()})}};
